import os
import fnmatch
from pathlib import Path

def get_files(basedir, patterns):
    files = []
    for path in Path(basedir).rglob('*'):
        if path.is_file():
            name = path.name
            for pattern in patterns:
                if fnmatch.fnmatch(name, pattern):
                    files.append(path)
    return files

def search(basedir, patterns, query):
    files = get_files(basedir, patterns)
    for path in files:
## COMPLETE CODE HERE
        if search_file(path, query):
            print("Found in", path)

def search_file(path, query):
    with open(path) as f:
        for line in f:
            if query in line:
                return True
    return False

if __name__ == '__main__':
    import sys
    query = sys.argv[1]
    patterns = sys.argv[2].split(',')
    basedir = sys.argv[3]
    search(basedir, patterns, query)

